sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api,event,helpers,imports}) {

    var row = parseInt(api.state.currentRow);
    var inputs = api.state.inputs;
    var word = "";

    switch (row) {
        case 1:
            {
                inputs = inputs.row1;
                word = inputs.c1 + inputs.c2 + inputs.c3 + inputs.c4 + inputs.c5;
                break;
            }
        case 2:
            {
                inputs = inputs.row2;
                word = inputs.c1 + inputs.c2 + inputs.c3 + inputs.c4 + inputs.c5;
                break;
            }
        case 3:
            {
                inputs = inputs.row3;
                word = inputs.c1 + inputs.c2 + inputs.c3 + inputs.c4 + inputs.c5;
                break;
            }
        case 4:
            {
                inputs = inputs.row4;
                word = inputs.c1 + inputs.c2 + inputs.c3 + inputs.c4 + inputs.c5;
                break;
            }
        case 5:
            {
                inputs = inputs.row5;
                word = inputs.c1 + inputs.c2 + inputs.c3 + inputs.c4 + inputs.c5;
                break;
            }
        case 6:
            {
                inputs = inputs.row6;
                word = inputs.c1 + inputs.c2 + inputs.c3 + inputs.c4 + inputs.c5;
                break;
            }
    }

    api.setState("userWord", word);

}
