sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    event = JSON.parse(JSON.stringify(event).replace("GlideRecord_Mutation", "mutation"));
    var recordID = event.payload.data.output.data.mutation.insert_x_317911_us_telc_0_daily_results.sys_id.value;
    api.setState("dailyStatusRecID", recordID);
    api.setState("patternURL", api.context.props.gtwResultDisplayPage + "?sysparm_rid=" + recordID);
    api.data.update_game_data.execute({
        recordId: recordID,
        data: api.state.gamePattern.value
    });
}
